package com.mango.user.config.exception;import lombok.Data;import lombok.EqualsAndHashCode;/** * 业务处理失败异常 * */@Data@EqualsAndHashCode(callSuper = false)public class ServiceException extends RuntimeException {    private static final long serialVersionUID = 5476063831102498723L;    /**     * 默认错误代码     */    public static final Integer ERROR_CODE = 0;    /**     * 错误代码     */    private Integer code;    /**     * 错误消息     */    private String message;    /**     * 接收 message 参数用来描述业务失败原因     */    public ServiceException(String message) {        super(message);        this.code = ERROR_CODE;    }}